    //     _serviceIots[_serviceId][_macAddress] = Iot(
    //         _description,      
    //         true
    //     );

    //     emit ServiceIotUpdate(_serviceId, _macAddress, "Iot added", msg.sender); 

    //     _services[_serviceId].iotIdCounter.increment();   
    // }

